import React, { useState,useEffect } from 'react';
import HeroesList from '../shared/heroes';
import HeroInfo from './HeroInfo';
import loadingGif from '../imagens/loading.gif';




function Content() {
    const [listOfHeroes, setListOfHeroes] = useState(HeroesList);
    const [favoriteHeroes, setFavoriteHeroes] = useState([1, 5, 6]);
    

    const favHeroes = () => {
    const fav1 = Math.round(Math.random() * HeroesList.length);
    const fav2 = Math.round(Math.random() * HeroesList.lenght);
    const fav3 = Math.round(Math.random() * HeroesList.lenght);

    setFavoriteHeroes([fav1, fav2, fav3]);
    
  }

  return (
    <>
  
       {HeroesList.map((item, pos) => {
                if (favoriteHeroes.includes(item.id)) {
                  return (
                      <HeroInfo key={pos} imagem={item.image} nome={item.name}></HeroInfo>
                  );
                }
              })}
              
              <button onClick={() => favHeroes() }>Heróis Aleatórios</button> 
    </>
  );


//.............................................................
//Ficha 4
const Loader = () => {
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setTimeout(() => {
      setLoading(false);
    }, 3000);
  }, []);

  return (
    <div>
      {loading ? 
        <div className="loader">
          <img src={loadingGif} alt="Loading..." />
          <h3>Loading...</h3>
        </div>
        : null}
    </div>
  );
}
}
export default Loader;
