/* eslint-disable eqeqeq */
import React from "react";
import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";

function Form (props){
    const {heroes} = props
    const param = useParams();

    const [hero, setHero] = useState({
    id: null,
    name: "",
    image_url: "",
    super_power: "",
  });

  useEffect(() => {
    if (param.id != undefined) {
      const heroj = heroes.find((heroj) => heroj.id == param.id);
      setHero(heroj);
    }
  }, [param.id, heroes]);

  const Change = (e) => {
    const { name, value } = e.target;
    setHero({ ...hero, [name]: value });
  };
   const Click = (e) => {
    if (param.id != undefined) {
      props.updateHero(e, hero);
    } else {
      props.createHero(e, hero);
    }
  };

  return(
    <>
    <form>
        <div className="text">
          <h1> {param.id || "Novo Hero"}</h1>
        </div>

        <label>Nome Hero:</label>
        <input className="form-input" type="text" id="name" name="name" value={hero.name} onChange={Change}/>

        <label>Imagem URL hero:</label>
        <input className="form-input" type="text" id="image_url" name="image_url" value={hero.image_url} onChange={Change}/>

        <label>Super Power:</label>
        <input type="text" id="super_power" name="super_power" value={hero.super_power} onChange={Change}/>
        <button onClick={Click}> {param.id != undefined ? "Guardar" : "Criar novo"}</button>
    </form>
    </>
  );
}
