import React, { useState,useEffect } from 'react';
import HeroesList from '../shared/heroes';
import HeroInfo from './HeroInfo';
import Loader from './Loader';
import { Routes, Route } from 'react-router-dom';
import Dashboard from './Dashboard';

const Content= () => {
    const [listOfHeroes, setListOfHeroes] = useState();
    const [favoriteHeroes, setFavoriteHeroes] = useState();
    const [loading, setLoading] = useState(true);
        
      useEffect(() => {
        setTimeout(() => {
          setLoading(false);
        }, 3000);
      }, []);

      if(loading == true){
        return(
          <div>
            <Loader/>
          </div>
        )
      }
      else{
      return (
        <>
        <Routes>
          <Route path="/" element={
            <>
            {listOfHeroes.map((item, pos) => {
                      if (favoriteHeroes.includes(item.id)) {
                        return (
                            <HeroInfo key={pos} imagem={item.image} nome={item.name}></HeroInfo>
                        );
                      }
                    })}
            </>
            }/>     
  
        {/* <Route path="/dashboard" element={<Dashboard heroes={listOfHeroes} heroesfav = {favoriteHeroes} favoriteHero = {favoriteHero} deleteHero= {deleteHero} />}/> */}
        {/* <Route path="/dashboard/add" element={<Heroform createHero={SubmitNewHero} />}/>
        <Route path="/dashboard/edit/:id" element={<Heroform heroes = {heroes} updateHero={SubmitEditHero} />}/> */}
        </Routes>
    </>
      )};
}

export default Content;
