import React, { useState } from 'react';
import HeroesList from '../shared/heroes';
import HeroInfo from './HeroInfo';


function Content() {
    const [heroes, setListOfHeroes] = useState(HeroesList);
    const [favoriteHeroes, setFavoriteHeroes] = useState([1, 5, 6]);

  const favHeroes = () => {
    const fav1 = Math.round(Math.random() * heroes.length);
    const fav2 = Math.round(Math.random() * heroes.lenght);
    const fav3 = Math.round(Math.random() * heroes.lenght);

    setFavoriteHeroes([fav1, fav2, fav3]);
  }


  
  return (
    <>
       {heroes.map((item, pos) => {
                if (favoriteHeroes.includes(item.id)) {
                  return (
                      <HeroInfo key={pos} imagem={item.image} nome={item.name}></HeroInfo>
                  );
                }
              })}

              <button onClick={() => favHeroes() }>Heroes Aleat√≥rios</button>
        ]
    </>
  );
}

export default Content;
