//Ficha 4
const Loader = () => {
  const [listOfHeroes, setListOfHeroes] = useState(HeroesList);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setTimeout(() => {
      setLoading(false);
    }, 3000);
  }, []);

  return (
    <div>
      {loading ? 
        <div className="loader">
          <img src={loadingGif} alt="Loading..." />
          <h3>Loading...</h3>
        </div>
        : null}
    </div>
  );
}

export default Loader;
//--------------------------------------------------------------------

// Ficha 6

const Content = () => {
  return (
    <main>
      <Routes>
        <Route path="/" element={<CurrentContent />} />
        <Route path="/dashboard" element={<UnderConstructionMessage />} />
      </Routes>
    </main>
  );
}

const CurrentContent = () => {
  return (
    <div>
      {/*Current content of the page goes here*/}
    </div>
  );
}

const UnderConstructionMessage = () => {
  return (
    <div>
