import React, { useState, useEffect } from 'react';
import HeroesList from './HeroesList';
import Loader from './Loader';

function Content() {
  const [listOfHeroes, setListOfHeroes] = useState(HeroesList);
  const [favoriteHeroes, setFavoriteHeroes] = useState([1, 5, 6]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setTimeout(() => {
      setLoading(false);
    }, 3000);
  }, []);

  return (
    <div>
      {loading ? <Loader /> : null}
      <button onClick={handleRandomizeFavorites}>Mudar Favoritos</button>
      {listOfHeroes.map((hero, pos) => {
        if (favoriteHeroes.includes(hero.id)) {
          return <HeroInfo key={pos} imagem={item.image} nome={item.name}></HeroInfo>
        }
      })}
    </div>
  );
}

export default Content;
