import React, { useState } from 'react';
import heroesList from '../shared/heroes';
import HeroInfo from './HeroInfo';

function Content() {
  const [heroesList, setListOfHeroes] = useState(HeroesList);
  const [favoriteHeroes, setFavoriteHeroes] = useState([1, 5, 6]);

  const generateRandomHeroes = () => {
    const randomHeroIds = [];
    while (randomHeroIds.length < 3) {
      const randomId = Math.floor(Math.random() * HeroesList.length) + 1;
      if (!randomHeroIds.includes(randomId)) {
        randomHeroIds.push(randomId);
      }
    }
    setFavoriteHeroes(randomHeroIds);
  };

  return (
    <div>
      <button onClick={generateRandomHeroes}>Mudar Favoritos</button>
      {heroesList
        .filter(hero => favoriteHeroes.includes(hero.id))
        .map(hero => (
          <HeroInfo key={hero.id} hero={hero} />
        ))}
    </div>
  );
}

export default Content;
