import loadingGif from '../imagens/loading.gif';

const Loader = () => {
    const [listOfHeroes, setListOfHeroes] = useState(HeroesList);
    const [loading, setLoading] = useState(true);
  
    useEffect(() => {
      setTimeout(() => {
        setLoading(false);
      }, 3000);
    }, []);
  
    return (
      <div>
        {loading ? 
          <div className="loader">
            <img src={loadingGif} alt="Loading..." />
            <h3>Loading...</h3>
          </div>
          : null}
      </div>
    );
  }
  
