import React, { useState } from 'react';
import HeroInfo from './HeroInfo';
import HeroesList from '../shared/heroes';





export default Content;


function Content() {
    const [list_of_heroes, setListOfHeroes] = useState(HeroesList);
  
    return (
      <div>
        {list_of_heroes.map(hero => (
          <div key={hero.id}>
            <h2>{hero.name}</h2>
            <p>{hero.description}</p>
          </div>
        ))}
      </div>
    );
  }
