import React, { useState } from 'react';
import HeroesList from '../shared/heroes';
import HeroInfo from './HeroInfo';


function Content() {
    const [listOfHeroes, setListOfHeroes] = useState(HeroesList);
    const [favoriteHeroes, setFavoriteHeroes] = useState([1, 5, 6]);

    const handleRandomizeFavorites = () => {
      let newFavorites = [];
      while (newFavorites.length < 3) {
        let randomId = Math.floor(Math.random() * HeroesList.length) + 1;
        if (!newFavorites.includes(randomId)) {
          newFavorites.push(randomId);
        }
      }
      setFavoriteHeroes(newFavorites);
    }
  
  return (
        <div>

          <button onClick={handleRandomizeFavorites}>Mudar Favoritos</button>

          {listOfHeroes.map((hero, index) => {
            if (favoriteHeroes.includes(hero.id)) {
              return <HeroInfo key={index} data={hero} />
            }
          })}
        </div>
    );
}
export default Content;
