import React, { useState,useEffect } from 'react';
import HeroInfo from './HeroInfo';
import Loader from './Loader';
import { Routes, Route, useNavigation } from 'react-router-dom';
import Dashboard from './Dashboard';
import  { GetHeroes, GetFavorites, UpdateHero, UpdateFavorites } from '../shared/api'

const Content= () => {
    const [listOfHeroes, setListOfHeroes] = useState();
    const [favoriteHeroes, setFavoriteHeroes] = useState();
    const [loading, setLoading] = useState(true);
    const navigate = useNavigation();

    useEffect(() => {
        GetHeroes().then(res => {
            setListOfHeroes(res)
        })
        GetFavorites().then(res => {
            setFavoriteHeroes(res)
        })
    }, []);
        
      useEffect(() => {
        setTimeout(() => {setLoading(false);}, 3000);
      }, []);

      useEffect(() => {
        if (loading != true){
            UpdateHero(listOfHeroes)
        }
    }, [listOfHeroes]);

    useEffect(() => {
        if (loading != true){
            UpdateFavorites(favoriteHeroes)
        }  
    }, [favoriteHeroes]);

     function FavoriteHero(id) {
        if (favoriteHeroes.includes(id)) {
            setFavoriteHeroes(favoriteHeroes.filter(favorite => favorite !== id));
        } else if (favoriteHeroes.length < 3) {
            setFavoriteHeroes([...favoriteHeroes, id]);
        }
    }
    function deleteHero(id) {
        setHeroes(heroes.filter(hero => hero.id !== id));
        if (heroesfav.includes(id)) {
            setFavs(heroesfav.filter(favorite => favorite !== id));
        }
    }

      if(loading == true){
        return(
          <div>
            <Loader/>
          </div>
        )
      }
      else{
      return (
        <>
        <Routes>
          <Route path="/" element={
            <>
            {listOfHeroes.map((item, pos) => {
                      if (favoriteHeroes.includes(item.id)) {
                        return (
                            <HeroInfo key={pos} imagem={item.image} nome={item.name}></HeroInfo>
                        );
                      }
                    })}
            </>
            }/>     
  
        <Route path="/dashboard" element={<Dashboard heroes={listOfHeroes} heroesfav = {favoriteHeroes} /*favoriteHero = {favoriteHero} deleteHero= {deleteHero}*/ />}/>
        {/* <Route path="/dashboard/add" element={<Heroform createHero={SubmitNewHero} />}/>
        <Route path="/dashboard/edit/:id" element={<Heroform heroes = {heroes} updateHero={SubmitEditHero} />}/> */}
        </Routes>
    </>
      )};
}

export default Content;
