}

const Dashboard = () => {
  return (
    <div>
    <h1>Dashboard de Super-Heróis</h1>
    <table>
      <thead>
        <tr>
          <th>ID</th>
          <th>Imagem</th>
          <th>Nome</th>
          <th>Superpoder</th>
        </tr>
      </thead>
      <tbody>
        {this.props.heroes.map(hero => (
          <tr key={hero.id}>
            <td>{hero.id}</td>
            <td>{hero.image}</td>
            <td>{hero.name}</td>
            <td>{hero.power}</td>
          </tr>
        ))}
      </tbody>
    </table>
  </div>
  );
}

export default Content;
