import React, { useState } from 'react';
import HeroInfo from './HeroInfo';
import HeroesList from '../shared/heroes';

function Content() {
    const [list_of_heroes, setListOfHeroes] = useState(HeroesList);
    const [favoriteHeroes, setFavoriteHeroes] = useState([1, 5, 6]);
  
    return (
      <div>
        <HeroInfo
            image="https://people.com/thmb/O4au7o8qtmbV9qC2SMQfPm7rg6k=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc():focal(675x379:677x381)/gal-gadot-2000-f893bc7afe9b44be91663eb17cc17750.jpg"
            name="Wonder woman"
        />
        <HeroInfo
            image="https://i.pinimg.com/originals/b9/09/b1/b909b1b9e13196d8b6e3e7f803b65904.jpg"
            name="Thor"
        />
        <HeroInfo
            image="https://image.winudf.com/v2/image/Y29tLmFuZHJvbW8uZGV2NjYwNjE0LmFwcDc5NTQwMV9zY3JlZW5fMF8xNTI2NjEwODY1XzAxOQ/screen-0.jpg?fakeurl=1&type=.webp"
            name="Spider Man"
        />

        {list_of_heroes.map(hero => (
          <div key={hero.id}>
            <h2>{hero.name}</h2>
            <p>{hero.description}</p>
          </div>
        ))}

        {listOfHeroes
        .filter(hero => favoriteHeroes.includes(hero.id))
        .map(hero => (
          <HeroInfo key={hero.id} hero={hero} />
        ))}
      </div>
    );
  }
  
  export default Content;
